using System;
using Package.Etermax.EterChallenge.Users.Domain;
using UniRx;

namespace Package.Etermax.EterChallenge.Users.Actions
{
    public class ChangeEmailPassword
    {
        private readonly IChangeEmailPasswordService _changeEmailPasswordService;

        public ChangeEmailPassword(IChangeEmailPasswordService changeEmailPasswordService)
        {
            _changeEmailPasswordService = changeEmailPasswordService;
        }

        public virtual IObservable<Unit> Execute(string email, string newPassword)
        {
            return _changeEmailPasswordService.ChangePassword(email, newPassword);
        }
    }
}