using System;
using Package.Etermax.EterChallenge.Users.Domain;
using UniRx;

namespace Package.Etermax.EterChallenge.Users.Actions
{
    public class CreateUser
    {
        private readonly ICreateUserService _createUserService;
        private readonly SessionManager _sessionManager;

        public CreateUser(ICreateUserService createUserService, SessionManager sessionManager)
        {
            _createUserService = createUserService;
            _sessionManager = sessionManager;
        }

        public virtual IObservable<User> Execute()
        {
            return _createUserService
                .Execute()
                .Do(_sessionManager.SetNewUser);
        }
    }
}