using System;
using Package.Etermax.EterChallenge.Users.Domain;
using UniRx;

namespace Package.Etermax.EterChallenge.Users.Actions
{
    public class ResetPassword
    {
        private readonly IResetPasswordService _resetPasswordService;

        public ResetPassword(IResetPasswordService resetPasswordService)
        {
            _resetPasswordService = resetPasswordService;
        }

        public virtual IObservable<Unit> Execute(string email)
        {
            return _resetPasswordService.ResetPassword(email);
        }
    }
}