using Etermax.Platform;
using Package.Etermax.EterChallenge.Users.Domain;

namespace Package.Etermax.EterChallenge.Users
{
    public static class PlatformServicesExtension
    {
        private static UsersService _usersService;

        public static UsersService Users(this PlatformServices platformServices)
        {
            return _usersService ?? (_usersService = new UsersService(platformServices, SessionManager.Default));
        }
    }
}