using Etermax.Web.Client;

namespace Etermax.Platform
{
    public class PlatformServices
    {
        public readonly string ApiUrl;
        public readonly string GameId;
        public readonly IWebClient WebClient;
        
        public static PlatformServices Instance { get; private set; }

        private PlatformServices(string apiUrl, string gameId, IWebClient webClient)
        {
            ApiUrl = apiUrl;
            GameId = gameId;
            WebClient = webClient;
        }

        public static PlatformServices With(string apiUrl, string gameId, IWebClient webClient)
        {
            Instance = new PlatformServices(apiUrl, gameId, webClient);
            return Instance;
        }
    }
}