using System;
using UniRx;

namespace Etermax.Rx.UnityDelivery.Widgets
{
    public class PositionTweener : VectorTweener
    {
        protected override IObservable<Unit> PlayTween()
        {
            return Tween()
                .Do(position => transform.localPosition = position)
                .AsUnitObservable();
        }
    }
}
