using System;
using UniRx;
using UnityEngine;

namespace Etermax.Rx.UnityDelivery.Widgets
{
    public class RotationTweener : FloatTweener
    {
        protected override IObservable<Unit> PlayTween()
        {
            return Tween()
                .Do(Rotate)
                .AsUnitObservable();
        }

		void Rotate(float z)
		{
            var angles = transform.localRotation.eulerAngles;
            var x = angles.x;
            var y = angles.y;

            transform.rotation = Quaternion.Euler(x, y, z);
		}
    }
}
