using System;
using UniRx;

namespace Etermax.Rx.UnityDelivery.Widgets
{
    public class ScaleTweener : VectorTweener
    {
        protected override IObservable<Unit> PlayTween()
        {
            return Tween()
                .Do(scale => transform.localScale = scale)
                .Select(_ => Unit.Default);
        }
    }
}
