using Etermax.Canvases.UnityDelivery.Provider;
using UnityEngine;

namespace Etermax.Canvases.UnityDelivery.Builder {
	public class CanvasBuilder {
		private string _id = "default";
		private string _name = "Default Canvas";
		private int _sortOrder;
		private int _widthResolution = 1536;
		private int _heightResolution = 2048;
		private RenderMode _renderMode = RenderMode.ScreenSpaceOverlay;
		private Camera _camera;

		public static CanvasBuilder GetCanvas() {
			return new CanvasBuilder();
		}

		public CanvasBuilder WithId(string id) {
			_id = id;
			return this;
		}

		public CanvasBuilder WithName(string name) {
			_name = name;
			return this;
		}

		public CanvasBuilder WithSortOrder(int sortOrder) {
			_sortOrder = sortOrder;
			return this;
		}

		public CanvasBuilder WithReferenceResolution(int width, int height) {
			_widthResolution = width;
			_heightResolution = height;
			return this;
		}

		public CanvasBuilder WithRenderMode(RenderMode renderMode, Camera camera){
			_renderMode = renderMode;
			_camera = camera;
			return this;
		}

		public Canvas Create() {
			return CanvasProvider.CreateCanvasSystem(_id, _name, _sortOrder, _widthResolution, _heightResolution,
				_renderMode, _camera);
		}
	}
}