using Etermax.Canvases.UnityDelivery.Builder;
using UnityEditor;
using UnityEngine;

namespace Etermax.Canvases.UnityEditorDelivery.MenuItems {

	public class CanvasProviderEditor : Editor {
		[MenuItem("Etermax/Canvas Provider/Create Canvas")]
		public static void CreateCanvas() {
			CanvasBuilder
				.GetCanvas()
				.WithId("ViewsCanvas")
				.WithName("Views Canvas")
				.WithReferenceResolution(1024, 2048)
				.Create();
		}
		
		[MenuItem("Etermax/Canvas Provider/Create Canvas with ScreenSpaceCamera render mode")]
		public static void CreateCanvasWithCamera() {
			CanvasBuilder
				.GetCanvas()
				.WithId("ViewsCanvas")
				.WithName("Views Canvas")
				.WithReferenceResolution(1024, 2048)
				.WithRenderMode(RenderMode.ScreenSpaceCamera, Camera.main)
				.Create();
		}

		//TODO For future configurations
