using System.Collections.Generic;
using Newtonsoft.Json;
using UnityEngine;

namespace Etermax.EditorExtensions.Data
{
    public class EditorData : ScriptableObject
    {
        [SerializeField] private string _json;
        private Dictionary<string, object> _dictionary = new Dictionary<string, object>();

        public void OnLoad()
        {
            _dictionary = JsonConvert.DeserializeObject<Dictionary<string, object>>(_json);
        }

        public void Add(string key, object value)
        {
            _dictionary.Add(key, value);
            _json = JsonConvert.SerializeObject(_dictionary);
        }

        public string GetString(string key)
        {
            return (string) _dictionary[key];
        }

        public bool ContainsKey(string key)
        {
            return _dictionary.ContainsKey(key);
        }
    }
}