using System.Collections.Generic;

namespace Etermax.Json.Extensions{
	public static class EnumerableExtensions{
		public static JsonArray<T> ToJsonArray<T>(this IEnumerable<T> source){
			var jsonArray = JsonArray<T>.Create();
			foreach (var element in source) {
				jsonArray.Add(element);
			}

			return jsonArray;
		}
	
		public static JsonObject ToJsonObject<T>(this IEnumerable<KeyValuePair<string, T>> source){
			var jsonObject = JsonObject.Create();
			foreach (var kvp in source) {
				jsonObject.Add(kvp.Key, kvp.Value);
			}

			return jsonObject;
		}
	}
}