using Etermax.Web.Client.Methods;

namespace Etermax.Web.Client.Editor.Mocks{
	public class WebClientMethodCallInfo{
		private readonly WebClient _webClient;
		private readonly StubHttpWebRequest _request;
		public HttpMethod Method { get; private set; }
		public string Url { get; private set; }

		public WebClientMethodCallInfo(WebClient webClient, HttpMethod method, string url,
			StubHttpWebRequest request){
			_webClient = webClient;
			_request = request;
			Method = method;
			Url = url;
		}

		public bool MatchesWithArgument(string uri){
			return Url == uri;
		}

		public IHttpWebRequest Response(){
			return _request;
		}
	}
}