using System;
using System.Collections.Generic;
using System.Net;

namespace Etermax.Web.Client.Error {
	public class HttpWebResponseError : Exception
	{
		public int StatusCode { get; private set; }
		public string Body  { get; private set; }
		public byte[] RawBody { get; private set; }
		public Dictionary<string, string> ResponseHeaders { get; private set; }

		public HttpWebResponseError(int statusCode, string body, byte[] rawBody, Dictionary<string, string> responseHeaders)
		{
			StatusCode = statusCode;
			Body = body;
			RawBody = rawBody;
			ResponseHeaders = responseHeaders ?? new Dictionary<string, string>();
		}
	}
}