
using System.Collections.Generic;

namespace Etermax.Web.Client
{
    public interface IWebClient
    {
        void SetRequestProxy(IRequestProxy requestProxy);
        void SetRequestProxies(List<IRequestProxy> requestProxies);
        void SetResponseProxy(IResponseProxy responseProxy);
        IHttpWebRequest Get(string uri);
        IHttpWebRequest Post(string uri);
        IHttpWebRequest Delete(string uri);
        IHttpWebRequest Put(string uri);
    }
}