using UnityEngine.Networking;

namespace Etermax.Web.Client
{
    public class UnityCertificateHandler : CertificateHandler
    {
        private readonly ICertificateHandler _certificateHandler;

        public UnityCertificateHandler(ICertificateHandler certificateHandler)
        {
            _certificateHandler = certificateHandler;
        }

        protected override bool ValidateCertificate(byte[] certificateData)
        {
            return _certificateHandler.ValidateCertificate(certificateData);
        }
    }
}