using System;
using UniRx;
using UnityEngine;
using UnityEngine.UI;

namespace Etermax.ViewNavigator.Domain {
    public class TabBarButton : MonoBehaviour {
        public GameObject Badge;
        public Text BadgeText;
        public Button DockNavButton;
        public Animator Animator;

        public IObservable<Unit> OnClick() {
            return DockNavButton.OnClickAsObservable().ThrottleFirst(TimeSpan.FromSeconds(1));
        }

        public void Activate() {
            Animator.SetBool("Active", true);
        }

        public void Deactivate() {
            Animator.SetBool("Active", false);
        }
    }
}