namespace Etermax.ViewNavigator.Infrastructure.Editor.Utilities
{
    public static class CreationDirectories
    {
        public const string DefaultRootFolderPath = "Assets/Presentation";
        public static string RootFolderPath = DefaultRootFolderPath;

        public static string ViewScriptFolderPath(string viewName)
        {
            return string.Format("{0}/{1}/UnityDelivery/Views/", RootFolderPath, viewName);
        }

        public static string ViewPrefabFolderPath(string viewName)
        {
            return string.Format("{0}/{1}/UnityDelivery/Prefabs/", RootFolderPath, viewName);
        }
        
        public static string ViewPrefabFolderPath(string viewName, string prefabName)
        {
            return string.Format("{0}/{1}/UnityDelivery/Prefabs/" + prefabName, RootFolderPath, viewName);
        }

        public static string ProvidersScriptFolderPath(string viewName)
        {
            return string.Format("{0}/{1}/Providers/", RootFolderPath, viewName);
        }

        public static string PresenterScriptFolderPath(string viewName)
        {
            return string.Format("{0}/{1}/Presentation/", RootFolderPath, viewName);
        }

        public static string ViewInterfaceFolderPath(string viewName)
        {
            return string.Format("{0}/{1}/Presentation/", RootFolderPath, viewName);
        }

        public static void ResetDefaultRootFolderPath()
        {
            RootFolderPath = DefaultRootFolderPath;
        }
    }
}
