using System;
using UnityEngine;

namespace Etermax.ViewNavigator.Infrastructure.ScripteableObjects
{
    [Serializable]
    public class ViewSetting
    {
        public string ViewName;
        public MonoBehaviour ViewMonoBehaviour;

        public ViewSetting(string viewName, MonoBehaviour viewMonoBehaviour)
        {
            ViewName = viewName;
            ViewMonoBehaviour = viewMonoBehaviour;
        }
    }
}